interface Props {
  children: JSX.Element | JSX.Element[];
}

const Card: React.FunctionComponent<Props> = props => (
  <div className="card">
    {props.children}
    <style jsx>{`
      .card {
        border-radius: 50px;
        background: #ffffff;
        box-shadow: 20px 20px 60px #d9d9d9, -20px -20px 60px #ffffff;
      }
    `}</style>
  </div>
);

export default Card;
