import { useEffect, useRef } from 'react';
import Prism from 'prismjs';

const CodeBlock = ({ language = '', value }) => {
  const codeEl = useRef(null);
  const highlightCode = () => {
    Prism.highlightAll();
  };

  useEffect(() => {
    highlightCode;
  });

  return (
    <pre>
      <code ref={codeEl} className={`language-${language}`}>
        {value}
      </code>
    </pre>
  );
};

export default CodeBlock;
