import matter, { GrayMatterFile } from 'gray-matter';
import { NextPage } from 'next';

import BlogList from '../components/BlogList';
import Layout from '../components/Layout';
import { ReactNode } from 'react';

export interface Article {
  document: GrayMatterFile<any>;
  slug: string;
}

interface Props {
  articles: Article[];
}

const Index = (props: NextPage<Props>): JSX.Element => (
  <Layout>
    <h1>The Blog</h1>
    <BlogList articles={props.articles} />
  </Layout>
);

Index.getInitialProps = async function(): Promise<Props> {
  const siteConfig = await import(`../data/config.json`);
  const context = require.context('../articles', true, /\.md$/);
  const keys = context.keys();
  const values = keys.map(context);
  console.log(values);
  const articles = keys.map(
    (key: string, index: number): Article => {
      const slug = key
        .replace(/^.*[\\\/]/, '')
        .split('.')
        .slice(0, -1)
        .join('.');
      const value = values[index];
      const document = matter(value.default);
      return {
        document,
        slug,
      };
    }
  );
  return {
    articles,
    ...siteConfig.default,
  };
};

export default Index;
