import { useEffect } from 'react';
import Prism from 'prismjs';

const CodeBlock = ({ language = '', value = '' }) => {
  const highlightCode = () => {
    Prism.highlightAll();
  };

  useEffect(() => {
    highlightCode();
  }, [highlightCode]);

  return (
    <pre>
      <code className={`language-${language}`}>{value}</code>
    </pre>
  );
};

export default CodeBlock;
